INSERT INTO `definitions` (`YDMS_Def_id`, `term`, `definition`, `YDMS_Inst_id`) VALUES
("YDMS_Def_0001", "Abuja Treaty ", "Abuja Treaty means the Treaty Establishing the African Economic Community adopted at Abuja, Nigeria on the 3rd day of June, 1991 and which entered into force on 12 May 1994", "inst_1"),
("YDMS_Def_0002", "ADR Panel ", "ADR Panel means a panel of conciliators or mediators;", "inst_5"),
("YDMS_Def_0003", "Aeronautical Authority", "Aeronautical Authority: means any Governmental authority, body corporate or organ duly authorised to perform any function to which these Regulations relate.", "inst_2"),
("YDMS_Def_0004", "AFCAC", "AFCAC- African Civil Aviation Commission;", "inst_6"),
("YDMS_Def_0005", "AFRAA", "AFRAA- African Airlines Association;", "inst_6"),
("YDMS_Def_0006", "African air transport legal instruments ", "African air transport legal instruments means the Yamoussoukro Decision, Annexes to the said Decision, decisions of the African Union, the Regional Economic Communities and the Executing Agency of the Yamoussoukro Decision relating to the application of the YD and its Annexes, the Single African air transport market;", "inst_5"),
("YDMS_Def_0007", "Air  Operator Certificate or AOC ", "Air  Operator Certificate or AOC – means A certificate authorising an operator to carry out specified commercial air transport operations.", "inst_6"),
("YDMS_Def_0008", "Air Services and Airlines ", "Air Services and Airlines have the meaning respectively assigned to them in  Article 96 of the Chicago Convention on International Civil Aviation dated 7th December 1944  Article 96 of the Chicago Convention:   For the purpose of this Convention the expression- (a) Air Services means any scheduled air  service performed by aircraft for the public transportation  of passengers, mail or cargo.  (b) International air service means and air service which passes through the air space over the territory of more than one State.  (c) An international Airline means any air transport enterprise offering or operating an international air service.", "inst_1"),
("YDMS_Def_0009", "Air Transport Service Providers ", "Air Transport Service Providers shall include airports, air navigation service providers, airport ground passenger and cargo handling companies, travel agents, suppliers of computer reservations systems or global distribution systems, and all other categories of services provided to airlines directly at the airports.", "inst_2"),
("YDMS_Def_0010", "Air transport services", "Air transport services means any scheduled or unscheduled air service performed by aircraft for the public transport of passengers, mail or cargo", "inst_4"),
("YDMS_Def_0011", "Air transport undertakings", "Air transport undertakings includes airlines and other air transport service providers.", "inst_3"),
("YDMS_Def_0012", "Aircraft services ", "Aircraft services comprise the external and internal cleaning of the aircraft, and the toilet and water services; the rearrangement of the cabin with suitable cabin equipment, the storage of this equipment.", "inst_4"),
("YDMS_Def_0013", "Airline ", " Airline means an air transport enterprise holding a valid Air Operators Certificate and operating air transport services within the territory of a State Party.", "inst_4"),
("YDMS_Def_0014", "Airport ", " Airport means any area of land especially adapted for the landing, taking-off and manoeuvres of aircraft, including the ancillary installations which these operations may involve for the requirements of aircraft traffic and services including the installations needed to assist commercial air services.", "inst_4"),
("YDMS_Def_0015", "Assembly ", "Assembly means the Assembly of Heads of State and Government of the Union;", "inst_5"),
("YDMS_Def_0016", "AU", "AU- African Union;", "inst_6"),
("YDMS_Def_0017", "AU Clause", "AU Clause- African Union Clauses for inclusion in Air Service Agreements with third countries;", "inst_6"),
("YDMS_Def_0018", "Authorisation ", "Authorisation means an agreement granted by the Aeronautical Authority of a State Party to an air carrier in accordance with Regulation on authorisation of air carriers in Africa and access of air carriers to intra-African routes under the Single Air Transport Market, permitting it to provide air services as stated in the Agreement.", "inst_6"),
("YDMS_Def_0019", "Capacity ", " Capacity means the number of seats and cargo space offered to the general public on air services over a given period and in a given sector.", "inst_3"),
("YDMS_Def_0020", "Capacity share ", "Capacity share   the share of an Eligible Airline of a State Party expressed as a percentage of the total capacity in a bilateral relationship with another State Party.", "inst_1"),
("YDMS_Def_0021", "Cargo ", "Cargo means any property carried on an aircraft other than mail, stores and accompanied or mishandled baggage.", "inst_4"),
("YDMS_Def_0022", "Chicago Convention or Convention", " Chicago Convention or Convention- Convention on International Civil Aviation done at Chicago, 7 December 1944;", "inst_6"),
("YDMS_Def_0023", "Common African Position ", "Common African Position – the Guidelines, AU Clauses and the recommendations adopted at the Meeting of Ministers of Air Transport at Sun City, South Africa, 18th-19th May 2005;", "inst_6"),
("YDMS_Def_0024", "Competent authority", "Competent authority: means a body established in a State Party and charged with regulating competition in the air transport sector, or in absence of such an institution.", "inst_3"),
("YDMS_Def_0025", "Concerted practice", "Concerted practice:  means co-ordination between airlines that, without having reached the stage where an agreement properly so-called has been concluded, knowingly substitutes practical co-operation to the exclusion of competition;", "inst_3"),
("YDMS_Def_0026", "Constitutive Act", "Constitutive Act- the Instrument establishing the African Union done at Lomé́, 11th, July 2000;", "inst_6"),
("YDMS_Def_0027", "Consumer ", "Consumer in relation to passengers means the person who takes or agrees to purchase an airline ticket or take a tour package (i.e., a combination of air transportation and ground or cruise accommodations) or tour component (e.g., a hotel stay) (hereinafter, the principal contractor), or any person on whose behalf the principal contractor agrees to purchase the ticket, package or component (the other beneficiaries) or any person to whom the principal contractor or any of the other beneficiaries transfers the ticket, package or component (the transferee).", "inst_4"),
("YDMS_Def_0028", "Country of Origin ", "Country of Origin the territory of a State Party where air transport starts. ", "inst_1"),
("YDMS_Def_0029", "Decision ", "Decision means the Decision Relating to the Implementation of the Yamoussoukro Declaration concerning the Liberalization of Access to Air Transport Markets in Africa signed at Yamoussoukro on 14th day of November 1999 and adopted at Lomé́ on 11th July 2000 by the Assembly of Heads of States of the African Union/African Economic Community, including the Appendices and amendments. ", "inst_1"),
("YDMS_Def_0030", "Depository", " Depository: the Organization of African Unity/ Africian Union.", "inst_1"),
("YDMS_Def_0031", "Designated airline", "Designated airline: Eligible Airline designated by a State Party to exercise its traffic rights under this Decision.", "inst_1"),
("YDMS_Def_0032", "Disputing Parties", "Disputing Parties means State Parties, State-controlled entities, African intergovernmental organizations charged with the enforcement or management of the Yamoussoukro Decision and its Annexes, legal or private parties including but not limited to airlines, other air transport enterprises or air transport undertaking and air passenger rights protection associations, whether acting in their capacity as claimants or defendants in any dispute relating to the interpretation or application of the relevant African air transport legal instruments;", "inst_5"),
("YDMS_Def_0033", "Dominant position", "Dominant position: means a position of one or more airlines which enables them to prevent effective competition being maintained within the market or part thereof, by giving them the power to behave to an appreciable extent independently of their competitors, their suppliers, their customers or end users.", "inst_3"),
("YDMS_Def_0034", "EC ", "EC -European Commission;", "inst_6"),
("YDMS_Def_0035", "Effective control", "Effective control:   a relationship constituted by rights, contracts or any other means which, either separately or jointly confer the possibility of a State Party or Group of State Parties or their nationals to directly or indirectly exercise a decisive influence on the running of the business of the airline or the right to use all or a substantive part of the assets of the air carriers.", "inst_1"),
("YDMS_Def_0037", "Eligible airline ", " Eligible airline means any airline duly licensed by a State Party and authorised to lift and put down passengers, cargo and mail in the territory of one or more State Parties and actually operating the flights in question and Certificated as an eligible airline to operate under the terms of the Yamoussoukro Decision.", "inst_4"),
("YDMS_Def_0038", "Eligible airline ", " Eligible Airline-any African air transport company fulfilling the requirement set forth in Article 6, sub-paragraph 6.9 of the Decision.", "inst_6"),
("YDMS_Def_0039", "EU", "EU- European Union", "inst_6"),
("YDMS_Def_0040", "EU Clauses", "EU Clauses- Provisions contained in the ECJ Ruling No 847/2004 ;", "inst_6"),
("YDMS_Def_0041", "Excessive capacity ", "Excessive capacity means more capacity than that reasonably required on a route or in a given sector.", "inst_3"),
("YDMS_Def_0042", "Excessively high price ", "Excessively high price means the price of a service which bears no reasonable relation to the economic value of that service and reasonable profit margin;", "inst_3"),
("YDMS_Def_0043", "Excessively low price ", "Excessively low price means the price of a service which bears no reasonable relation to the economic value of those services.", "inst_3"),
("YDMS_Def_0044", "Executing Agency ", "Executing Agency means the African Air Transport Executing Agency provided for under Article 9 (4) of the Yamoussoukro Decision.", "inst_3"),
("YDMS_Def_0045", "Executive Council ", "Executive Council means the Executive Council of Ministers of the Union;", "inst_5"),
("YDMS_Def_0046", "External Policy and Guidelines or Guidelines ", "External Policy and Guidelines or Guidelines – External Policy, Guidelines and Binding clauses for the negotiation of air services agreements between African Union States and Non-African States and Regions.", "inst_6"),
("YDMS_Def_0047", "Fifth freedom traffic right", "Fifth freedom traffic right: the right of an Eligible Airline of one State Party to carry passengers, freight and mail between two State Parties other than the State Party in which it is licensed.", "inst_1"),
("YDMS_Def_0048", "Fourth freedom traffic right", "Fourth freedom traffic right:  the right of an Eligible Airline of one State Party to take on, in the territory of another State Party, passengers, freight and mail for off- loading in the State Party in which it is licensed.", "inst_1"),
("YDMS_Def_0049", "Ground handling ", "Ground handling means the services provided to airlines at airports and comprise the following passenger handling, baggage handling, freight handling, mail handling, ramp handling, oil and fuel handling, aircraft maintenance, flight operations and crew administration, and surface transport.", "inst_4"),
("YDMS_Def_0050", "Group", "Group- includes the Regional Economic Communities, Sub-Regional Group and/or a number of Member States;", "inst_6"),
("YDMS_Def_0051", "Guidelines", "Guidelines- This Guidelines and any Annexes therein;", "inst_6"),
("YDMS_Def_0052", "IATA", "IATA - International Air Transport Association ;", "inst_6"),
("YDMS_Def_0053", "ICAO", "ICAO- the International Civil Aviation Organization;", "inst_6"),
("YDMS_Def_0054", "Jointly-owned and jointly operated airline", "Jointly-owned and jointly operated airline:  any airline created by virtue of Article 77 of the Chicago Convention on International Civil Aviation;", "inst_1"),
("YDMS_Def_0055", "Licence ", " Licence means a valid licence granted by the Civil Aviation Authority or its equivalent under valid Regulations of States Parties to an air transport undertaking.", "inst_4"),
("YDMS_Def_0056", "Market ", "Market means a relevant geographic area, including routes or sector thereof and a relevant air transport service provided by an airline.", "inst_3"),
("YDMS_Def_0057", "Member State ", "Member State means a Member State of the African Union.", "inst_3"),
("YDMS_Def_0058", "Member States to guidelines", "Member States– State Parties to the Yamoussoukro Decision and such other African Countries which though not a party to the Abuja Treaty or the Yamoussoukro Decision, has declared in writing its intention to be bound by this Guideline;", "inst_6"),
("YDMS_Def_0059", "Non-African airline ", "Non-African airline means an airline licensed by a third party state and authorised by a State Party to lift and put down passengers, cargo and mail in the territory of one or more State Parties and actually operating the flight in question.", "inst_4"),
("YDMS_Def_0060", "Non-eligible airline ", "Non-eligible airline means any airline duly licensed by a State Party and authorised to operate intra-African routes but has not been certificated as an eligible airline to operate under the terms of the Yamoussoukro Decision.", "inst_4"),
("YDMS_Def_0061", "Organ of the African Union ", "Organ of the African Union shall refer to Organs of the African Union as provided for in the Constitutive Act of the African Union.", "inst_2"),
("YDMS_Def_0062", "Organ of the Decision ", "Organ of the Decision means the bodies in the Yamoussoukro Decision tasked with the supervision and follow up of its implementation and includes include the Executing Agency, the Monitoring Body, the Sub-Committee on Air Transport of the Committee on Transport, the Conference of African Ministers of Transport or any such body or bodies as are appointed to replace them by them.", "inst_2"),
("YDMS_Def_0063", "Party ", "Party means a party to the dispute and includes a person claiming through or under another party;", "inst_5"),
("YDMS_Def_0064", "RECs ", "RECs -The Regional Economic Communities of Africa, collectively called the Regional Economic Groups", "inst_6"),
("YDMS_Def_0065", "Regional Competition Authority ", "Regional Competition Authority means an authority set up by a regional economic community with mandate to regulate and supervise the implementation of these regulations.", "inst_3"),
("YDMS_Def_0066", "Regional Economic Community ", "Regional Economic Community means a regional economic community recognised as such by the African Union.", "inst_3"),
("YDMS_Def_0067", "Regional Yamoussoukro Decision Authority ", "Regional Yamoussoukro Decision Authority means the authority or agency established or constituted by a regional economic community recognized by the African Union under the Abuja Treaty and invested with powers to supervise and manage the implementation of the Yamoussoukro Decision within the region.", "inst_2"),
("YDMS_Def_0068", "Regional Yamoussoukro Decision Authority ", "Regional Yamoussoukro Decision Authority means an authority set up by a regional economic community with mandate to regulate and supervise the implementation of the Yamoussoukro Decision within the territory of the regional economic community concerned.", "inst_3"),
("YDMS_Def_0069", "Rulemaking ", "Rulemaking shall mean the development and issuance of rules by the Executing Agency. ", "inst_2"),
("YDMS_Def_0070", "Rules", "Rules comprises the following proposed to be developed by the Executing Agency: decisions, regulations and guidelines.", "inst_2"),
("YDMS_Def_0071", "Scheduled and non-scheduled air services ", "Scheduled and non-scheduled air services shall bear the same meaning assigned to them in the Chicago Convention of 1944 and in resolutions of the Council of the International Civil Aviation Organization (ICAO).", "inst_3"),
("YDMS_Def_0072", "Scheduled and Unscheduled air services", "Scheduled and Unscheduled air services:  as defined in Chicago Convention and ICAO Council resolutions.", "inst_1"),
("YDMS_Def_0073", "State Party ", "State Party means an African State signatory to the Abuja Treaty, to the Yamoussoukro Decision or to the Solemn Commitment towards the establishment of the SAATM or such other African Country which though not a party to the Abuja Treaty or the Yamoussoukro Decision, has endorsed these Guidelines", "inst_6"),
("YDMS_Def_0074", "Tariffs  ", "Tariffs   the prices to be paid for the carriage of passengers, baggage or cargo (excluding mail) on scheduled air services and the conditions, under which these prices apply,  including  remuneration  and  conditions  offered  to  travel  agencies  and  other auxiliary services.", "inst_1"),
("YDMS_Def_0075", "Third country/Block of countries", "Third country/Block of countries-Non-African States and Regions or any State other than a State party", "inst_6"),
("YDMS_Def_0076", "Third freedom traffic right  ", "Third freedom traffic right  the right of an Eligible Airline of one State Party to put down, in the territory of another State Party, passengers, freight and mail taken up in the State Party in which it is licensed.", "inst_1"),
("YDMS_Def_0077", "Trade association ", "Trade association means an association of airlines with the aim of promoting co-operation activities of its members.", "inst_3"),
("YDMS_Def_0078", "Virtual airline", "Virtual airline: An airline or air carrier designated with the intend to circumvent traffic rights restrictions imposed by bilateral or multilateral Air Services Agreement clauses", "inst_6"),
("YDMS_Def_0079", "YD Text ", "YD Text – The Yamoussoukro Decision regulation on Competition, Consumer protection, Approval of air carriers and Market access, Dispute Settlement  including the Appendices and amendments.", "inst_6");
